import {DecodeError} from '@aureooms/js-codec';

export default class Base64DecodeError extends DecodeError {
	constructor(reason, object, position) {
		super('base64', reason, object, position);
	}
}

import {EncodeError} from '@aureooms/js-codec';

export default class Base64EncodeError extends EncodeError {
	constructor(reason, object, position) {
		super('base64', reason, object, position);
	}
}

import {iter, next, StopIteration} from '@aureooms/js-itertools';
import {ValueError} from '@aureooms/js-error';

import byte3tochar4 from './byte3tochar4.js';
import byte2tochar3 from './byte2tochar3.js';
import byte1tochar2 from './byte1tochar2.js';

import Base64DecodeError from './Base64DecodeError.js';

import variants from './variants.js';
import DEFAULT_OPTIONS from './DEFAULT_OPTIONS.js';

export default function* _decode(bytes, options = DEFAULT_OPTIONS) {
	if (options.variant) {
		if (Object.prototype.hasOwnProperty.call(variants, options.variant)) {
			options = variants[options.variant];
		} else {
			throw new ValueError(`unknown Base64 variant ${options.variant}`);
		}
	}

	let start = 0;

	const {alphabet, padding} = options;

	const it = iter(bytes);

	while (true) {
		let a;
		let b;
		let c;

		try {
			a = next(it);
		} catch (error) {
			if (error instanceof StopIteration) {
				break;
			} else {
				throw error;
			}
		}

		if (a < 0x00 || a > 0xff) {
			const reason = `byte out of range 0x00 <= ${a} <= 0xFF`;
			const position = {start, end: start + 1};
			throw new Base64DecodeError(reason, bytes, position);
		}

		++start;

		try {
			b = next(it);
		} catch (error) {
			if (error instanceof StopIteration) {
				yield* byte1tochar2(alphabet, a);
				if (padding) {
					yield padding;
					yield padding;
				}

				break;
			} else {
				throw error;
			}
		}

		if (b < 0x00 || b > 0xff) {
			const reason = `byte out of range 0x00 <= ${b} <= 0xFF`;
			const position = {start, end: start + 1};
			throw new Base64DecodeError(reason, bytes, position);
		}

		++start;

		try {
			c = next(it);
		} catch (error) {
			if (error instanceof StopIteration) {
				yield* byte2tochar3(alphabet, a, b);
				if (padding) {
					yield padding;
				}

				break;
			} else {
				throw error;
			}
		}

		if (c < 0x00 || c > 0xff) {
			const reason = `byte out of range 0x00 <= ${c} <= 0xFF`;
			const position = {start, end: start + 1};
			throw new Base64DecodeError(reason, bytes, position);
		}

		yield* byte3tochar4(alphabet, a, b, c);

		++start;
	}
}

import assert from 'assert';
import {iter, next, StopIteration, enumerate} from '@aureooms/js-itertools';
import {object, reflect} from '@aureooms/js-mapping';
import {ValueError} from '@aureooms/js-error';

import char4tobyte3 from './char4tobyte3.js';
import char3tobyte2 from './char3tobyte2.js';
import char2tobyte1 from './char2tobyte1.js';

import Base64EncodeError from './Base64EncodeError.js';

import variants from './variants.js';
import DEFAULT_OPTIONS from './DEFAULT_OPTIONS.js';

export default function* _encode(string, options = DEFAULT_OPTIONS) {
	if (options.variant) {
		if (Object.prototype.hasOwnProperty.call(variants, options.variant)) {
			options = variants[options.variant];
		} else {
			throw new ValueError(`unknown Base64 variant ${options.variant}`);
		}
	}

	let start = 0;

	const index = options.index || object(reflect(enumerate(options.alphabet)));
	const padding = options.padding;

	const it = iter(string);

	while (true) {
		let a;
		let b;
		let c;
		let d;

		let stop = 4;

		try {
			a = next(it);
		} catch (error) {
			if (error instanceof StopIteration) {
				break;
			} else {
				throw error;
			}
		}

		try {
			b = next(it);
		} catch (error) {
			if (error instanceof StopIteration) {
				const reason = `lone character ${a}`;
				const position = {start, end: start + 1};
				throw new Base64EncodeError(reason, string, position);
			} else {
				throw error;
			}
		}

		try {
			c = next(it);
		} catch (error) {
			if (error instanceof StopIteration) {
				if (padding) {
					const reason = `missing padding after ${a}${b}`;
					const position = {start, end: start + 2};
					throw new Base64EncodeError(reason, string, position);
				}

				stop = 2;
			} else {
				throw error;
			}
		}

		if (stop === 4) {
			try {
				d = next(it);
			} catch (error) {
				if (error instanceof StopIteration) {
					if (padding) {
						const reason = `missing padding after ${a}${b}${c}`;
						const position = {start, end: start + 3};
						throw new Base64EncodeError(reason, string, position);
					}

					stop = 3;
				} else {
					throw error;
				}
			}
		}

		if (padding) {
			stop = [a, b, c, d].indexOf(padding);
			stop = stop === -1 ? 4 : stop;
			if (stop < 2) {
				const reason = `padding at wrong position in ${a}${b}${c}${d}`;
				const position = {start: start + stop, end: start + stop + 1};
				throw new Base64EncodeError(reason, string, position);
			}

			if (stop === 2 && d !== padding) {
				const reason = `incorrect padding end in ${a}${b}${c}${d}`;
				const position = {start: start + 3, end: start + 4};
				throw new Base64EncodeError(reason, string, position);
			}
		}

		if (!Object.prototype.hasOwnProperty.call(index, a)) {
			const reason = `not in alphabet ${a}`;
			const position = {start, end: start + 1};
			throw new Base64EncodeError(reason, string, position);
		}

		if (!Object.prototype.hasOwnProperty.call(index, b)) {
			const reason = `not in alphabet ${b}`;
			const position = {start: start + 1, end: start + 2};
			throw new Base64EncodeError(reason, string, position);
		}

		if (stop > 2) {
			if (!Object.prototype.hasOwnProperty.call(index, c)) {
				const reason = `not in alphabet ${c}`;
				const position = {start: start + 2, end: start + 3};
				throw new Base64EncodeError(reason, string, position);
			}

			if (stop > 3) {
				if (!Object.prototype.hasOwnProperty.call(index, d)) {
					const reason = `not in alphabet ${d}`;
					const position = {start: start + 3, end: start + 4};
					throw new Base64EncodeError(reason, string, position);
				}
			} else if (index[c] & 3) {
				const reason = `trailing bits in ${a}${b}${c}, last digit should be ${
					options.alphabet[index[c] & (63 << 2)]
				}`;
				const position = {start: start + 2, end: start + 3};
				throw new Base64EncodeError(reason, string, position);
			}
		} else if (index[b] & 15) {
			const reason = `trailing bits in ${a}${b}, last digit should be ${
				options.alphabet[index[b] & (3 << 4)]
			}`;
			const position = {start: start + 1, end: start + 2};
			throw new Base64EncodeError(reason, string, position);
		}

		switch (stop) {
			case 4:
				yield* char4tobyte3(index, a, b, c, d);
				break;
			case 3:
				yield* char3tobyte2(index, a, b, c);
				break;
			case 2:
				yield* char2tobyte1(index, a, b);
				break;
			default:
				assert(false, `Unexpected stop value: ${stop}.`);
		}

		start += 4;
	}
}

export default function* byte1tochar2(alphabet, a) {
	yield alphabet[a >> 2];
	yield alphabet[63 & (a << 4)];
}

export default function* byte2tochar3(alphabet, a, b) {
	yield alphabet[a >> 2];
	yield alphabet[(63 & (a << 4)) | (b >> 4)];
	yield alphabet[63 & (b << 2)];
}

export default function* byte3tochar4(alphabet, a, b, c) {
	yield alphabet[a >> 2];
	yield alphabet[(63 & (a << 4)) | (b >> 4)];
	yield alphabet[(63 & (b << 2)) | (c >> 6)];
	yield alphabet[63 & c];
}

export default function* char2tobyte1(index, a, b) {
	yield (index[a] << 2) | (index[b] >> 4);
}

export default function* char3tobyte2(index, a, b, c) {
	yield (index[a] << 2) | (index[b] >> 4);
	yield (0xff & (index[b] << 4)) | (index[c] >> 2);
}

export default function* char4tobyte3(index, a, b, c, d) {
	yield (index[a] << 2) | (index[b] >> 4);
	yield (0xff & (index[b] << 4)) | (index[c] >> 2);
	yield (0xff & (index[c] << 6)) | index[d];
}

import _decode from './_decode.js';
import DEFAULT_OPTIONS from './DEFAULT_OPTIONS.js';

export default function decode(bytes, options = DEFAULT_OPTIONS) {
	return [..._decode(bytes, options)].join('');
}

import _encode from './_encode.js';
import DEFAULT_OPTIONS from './DEFAULT_OPTIONS.js';

export default function encode(bytes, options = DEFAULT_OPTIONS) {
	return [..._encode(bytes, options)];
}

import Base64DecodeError from './Base64DecodeError.js';
import Base64EncodeError from './Base64EncodeError.js';
import DEFAULT_OPTIONS from './DEFAULT_OPTIONS.js';
import _decode from './_decode.js';
import _encode from './_encode.js';
import byte1tochar2 from './byte1tochar2.js';
import byte2tochar3 from './byte2tochar3.js';
import byte3tochar4 from './byte3tochar4.js';
import char2tobyte1 from './char2tobyte1.js';
import char3tobyte2 from './char3tobyte2.js';
import char4tobyte3 from './char4tobyte3.js';
import decode from './decode.js';
import encode from './encode.js';
import variants from './variants.js';

/* eslint import/no-anonymous-default-export: [2, {"allowObject": true}] */
export default {
	decode,
	encode,
};

export {
	Base64DecodeError,
	Base64EncodeError,
	DEFAULT_OPTIONS,
	_decode,
	_encode,
	byte1tochar2,
	byte2tochar3,
	byte3tochar4,
	char2tobyte1,
	char3tobyte2,
	char4tobyte3,
	decode,
	encode,
	variants,
};

import {enumerate} from '@aureooms/js-itertools';

import {object, reflect} from '@aureooms/js-mapping';

const variants = {
	// Standard
	RFC4648: {
		alphabet:
			'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/',
		padding: '=',
	},

	// Unpadded base64url
	RFC7515: {
		alphabet:
			'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789-_',
		padding: false,
	},

	// YUI library
	Y64: {
		alphabet:
			'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789._',
		padding: '-',
	},

	// IMAP mailbox names
	RFC3501: {
		alphabet:
			'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+,',
		padding: false,
	},
};

for (const variant in variants) {
	if (Object.prototype.hasOwnProperty.call(variants, variant)) {
		variants[variant].index = object(
			reflect(enumerate(variants[variant].alphabet)),
		);
	}
}

export default variants;

