export {default as char4tobyte3} from './char4tobyte3.js';
export {default as decode} from './decode.js';
export {default as encode} from './encode.js';
export {default as variants} from './variants.js';

export {ValueError} from '@failure-abstraction/error';
export {CodecError} from '@codec-bytes/error';

import {enumerate} from '@iterable-iterator/zip';
import {toObject, inverse} from '@iterable-iterator/mapping';

const variants = {
	// Standard
	RFC4648: {
		alphabet:
			'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/',
		padding: '=',
	},

	// Unpadded base64url
	RFC7515: {
		alphabet:
			'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789-_',
		padding: false,
	},

	// YUI library
	Y64: {
		alphabet:
			'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789._',
		padding: '-',
	},

	// IMAP mailbox names
	RFC3501: {
		alphabet:
			'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+,',
		padding: false,
	},
};

for (const variant in variants) {
	if (Object.prototype.hasOwnProperty.call(variants, variant)) {
		variants[variant].index = toObject(
			inverse(enumerate(variants[variant].alphabet)),
		);
	}
}

export default variants;

